import Group from '../assets/imgs/Group 91.png';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../assets/css/Section2.css'

function HeroSection() {

    return (
        <>
            <div style={{ marginTop: '50px', background: 'linear-gradient(to bottom, #add8e6, #ffd700, #dda0dd, #000)' }}>
                <Container fluid="md" style={{ marginTop: '50px' }}>

                    <Row style={{ textAlign: 'center' }}>
                        <Col><h1>The Future of Web3 NoCode
                        </h1></Col>


                    </Row>

                    <Row style={{ textAlign: 'center' }}>
                        <Col><p>Web3Makr allows you to incorporate top ranked blockchain platforms into your work through the drag and drop feature.
                        </p></Col>
                    </Row>





                    <Row>


                        <Col sm={4}>
                            <Col><h1>Multi Chain Deshboard tool</h1></Col>
                        </Col>
                        <Col sm={8}>
                            <img style={{ width: '500px', height: '550px' }} src={Group} alt='kd' />
                        </Col>
                    </Row>







                </Container>

            </div >

        </>
    );
}

export default HeroSection;
