    return (
        <>
            <div className='contain-2'>
                <Container fluid="md" >

                    <Row style={{ textAlign: 'center' }}>
                        <Col><h1>The Future of Web3 NoCode
                        </h1></Col>


                    </Row>

                    <Row style={{ textAlign: 'center' }}>
                        <Col><p>Web3Makr allows you to incorporate top ranked blockchain platforms into your work through the drag and drop feature.
                        </p></Col>
                    </Row>






                    <Col sm={4}>

                    </Col>
                    <Col sm={8}>

                    </Col>








                </Container>

            </div >

        </>
    );
}

export default HeroSection;
